var mongoose = require('mongoose');
var Schema = mongoose.Schema;

const currentGameSchema = new Schema({
    pseudo1: String,
    pseudo2: String,
    gameBoard: [
        [Number]
    ],
    starter: String,
    turnOfPlayer: String
});

var CurrentGames = mongoose.model('currentgames', currentGameSchema);

module.exports = {
    CurrentGames: CurrentGames
}