var mongoose = require('mongoose');
var Schema = mongoose.Schema;

const currentGameSchema = new Schema({
    pseudo1: {
        type: String,
        required: true
    },
    pseudo2: {
        type: String,
        required: true
    },
    gameBoard: {
        type: [
            [Number]
        ]
    },
    starter: {
        type: String,
        required: true
    },
    turnOfPlayer: {
        type: String,
        required: true
    },
});

var CurrentGames = mongoose.model('currentgames', currentGameSchema);

module.exports = {
    CurrentGames: CurrentGames
}