var mongoose = require('mongoose');
var Schema = mongoose.Schema;

const finishedGameSchema = new Schema({
    pseudo1: {
        type: String,
        required: true
    },
    pseudo2: {
        type: String,
        required: true
    },
    winner: {
        type: String,
        required: true
    },
});

var FinishedGames = mongoose.model('finishedgames', finishedGameSchema);

module.exports = {
    FinishedGames: FinishedGames
}