var mongoose = require('mongoose');
var Schema = mongoose.Schema;

const topScoreSchema = new Schema({
    pseudo: {
        type: String,
        required: true
    },
    score: {
        type: Number,
        required: true
    },
});

var TopScores = mongoose.model('topscores', topScoreSchema);

module.exports = {
    TopScores: TopScores
}