var mongoose = require('mongoose');
var Schema = mongoose.Schema;

const userSchema = new Schema({
    pseudo: {
        type: String,
        required: true
    },
    password: {
        type: String,
        required: true
    },
    numberOfGamePlayed: {
        type: Number,
        default: 0
    },
    numberOfGameWon: {
        type: Number,
        default: 0
    }
});

var Users = mongoose.model('users', userSchema);

module.exports = {
    Users: Users
}